$(document).ready(function(){$('.nav-toggle').click(function(){$('.nav-list').toggleClass('is-open')})})
var didScroll
var lastScrollTop=0
var delta=5
var navbarHeight=$('header').outerHeight()
$(window).scroll(function(event){didScroll=!0})
setInterval(function(){if(didScroll){hasScrolled()
didScroll=!1}},250)
function hasScrolled(){var st=$(this).scrollTop()
console.log(st);if(Math.abs(lastScrollTop-st)<=delta)
return;if(st>lastScrollTop&&st>navbarHeight){$('header').removeClass('header-show').addClass('header-hide')}else{if(st+$(window).height()<$(document).height()){$('header').removeClass('header-hide').addClass('header-show')}}
lastScrollTop=st}